const App = props => {
  const [personsState, setPersonsState] = useState({
    persons: [
      {name: 'Max',     age: 23},
      {name: 'Moritz',  age: 26},
      {name: 'Pete',    age: 32}
    ]
  });

const [numbersState, setNumbersState] = useState({
  numbers: [
    11,22,33
  ]
});
const switchNumberHandler = () => {
  setNumbersState({
    numbers: [
      numbersState.numbers[0]+1,
      numbersState.numbers[1]+2,
      numbersState.numbers[2]+3
    ]
  });
};

const switchNameHandler = () => {
  setPersonsState({ 
    persons: [
      {name: 'Maxi',     age: 24},
      {name: 'Moritz',  age: 26},
      {name: 'Pete',    age: 32}
    ]
  });
};

    return (
    <div className="App">
      <h1>Hi, I'm a React App!!</h1>
      <button onClick={switchNameHandler}>Switch Name</button>
      <Person name={personsState.persons[0].name} age={personsState.persons[0].age} number={numbersState.numbers[0]}/>
      <Person name={personsState.persons[1].name} age={personsState.persons[1].age} number={numbersState.numbers[1]}/>
      <Person name={personsState.persons[2].name} age={personsState.persons[2].age} number={numbersState.numbers[2]}/>
    </div>
    )
